import React, { useState } from 'react';
import ListingSection from './components/CalorieRecords-Section/ListingSection';
import CaloriesRecordEdit from './components/edit/CaloriesRecordEdit';
import Model from "react-model";
const INITIAL_RECORDS = [
  { id: 1, date: new Date(2023, 2, 1), meal: "Breakfast", content: 'Eggs', calories: 111 },
  { id: 2, date: new Date(2023, 2, 2), meal: "Breakfast", content: 'Bas', calories: -111 },
  { id: 3, date: new Date(2023, 2, 3), meal: "Breakfast", content: 'Milk', calories: 333 },
  { id: 4, date: new Date(2023, 2, 4), meal: "Breakfast", content: 'Rice', calories: 444 },
];

function App() {
  const [records, setRecords] = useState(INITIAL_RECORDS);
  const [nextId, setNextId] = useState(5);
  const [isModalOpen,setIsModalOpen]=useState(false);

  const handleOpenModal=()=>{
    setIsModalOpen(true);
  }
  const handleCloseModal=()=>{
    setIsModalOpen(false);
  }

  const formSubmitHandler = (record) => {
    const formattedRecord = {
      ...record,
      date: record.date,  // استخدم التاريخ المدخل بدون تحويله مرة أخرى إلى `new Date()`
      id: nextId,
    };

    setNextId((lastVal) => lastVal + 1);
    setRecords((prevRecords) => [formattedRecord, ...prevRecords]);
    handleCloseModal();
  };

  return (
    <div>
      <h1>Welcome to React with Almdrasa!</h1>

      <CaloriesRecordEdit onFormSubmit={formSubmitHandler} />
      <ListingSection allRecords={records} />
    </div>
  );
}

export default App;
