import React from 'react';

// import {RecordList} from './components/RecordList';
import ListingSection from './components/CalorieRecords-Section/ListingSection';
import CaloriesRecordEdit from './components/edit/CaloriesRecordEdit';
import { useState } from 'react';

const INITIAL_RECORDS = [{
  id:1,
  date: new Date(2023, 2, 1,),
  meal: "Breakfast",
  content: 'Eggs',
  calories: 111,
},
{
  id:2,
  date: new Date(2023, 2, 2,),
  meal: "Breakfast",
  content: 'Bas',
  calories: -111,
},
{
  id:3,
  date: new Date(2023, 2, 3,),
  meal: "Breakfast",
  content: 'Milk',
  calories: 333,
},
{
  id:4,
  date: new Date(2023, 2, 4,),
  meal: "Breakfast",
  content: 'Rice',
  calories: 444,
},

]
function App() {
  const [records, setRecords] = useState(INITIAL_RECORDS);
  const [nextId, setNextId] = useState(5);


  const formSubmitHandler = (record) => {
    console.log('New Record:', record); // للتحقق من البيانات الواردة
    const formattedRecord = {
      ...record,
      date: new Date(record.date),
      id: nextId,
    };
    console.log('Formatted Record:', formattedRecord); // للتحقق من التنسيق

    setNextId((lastVal) => lastVal + 1);
    setRecords((prevRecords) => {
      console.log('Previous Records:', prevRecords); // للتحقق من السجلات السابقة
      return [formattedRecord, ...prevRecords];
    });
}



  return (
    <div>
      <h1>Welcome to React with Almdrasa!</h1>
      <CaloriesRecordEdit onFormSubmit={formSubmitHandler} />
      <ListingSection allRecords={records} />


    </div>
  )
}

export default App;
