import  style from "./CalorieRecord.modul.css";
import CalorieRecordDate from "./CalorieRecordDate";
import { useState } from "react";
import StyleRecordCell from "../common/StyleRecordCell";
function CalorieRecord(props) {
  const [currentCalories, setCurrentCalories] = useState(props.calories);
  const calorieClickHandler = () => {

    console.log("current Calories Event Fired")
    setCurrentCalories(currentCalories + 10);
  }

  return (
    <ul className="record">
      <li >
        <CalorieRecordDate date={props.date} />
      </li>
      <li>{props.meal}</li>
      <li>{props.content}</li>
      <li className="record-calories" onClick={calorieClickHandler}>
        <StyleRecordCell>{currentCalories}</StyleRecordCell>



      </li>
    </ul>
  );
}

export default CalorieRecord;
