import { useState } from "react";

import "./CaloriesRecordEdit.css";
import ClickCounter from "./ClickCounter";
import styled from "styled-components";

const Form = styled.form`



    background-color: #d4e0ff;
    padding: 20px;
    border-radius: 10px;

& label {
    color: #333;
    margin-right: 30px;
    margin-bottom: 10px;
  }
  
  & input[type="text"],
  & input[type="number"],
  & input[type="date"] {
    background-color: #333;
    color: #fff;
    border: none;
    padding: 10px;
    border-radius: 5px;
    width: 100%;
    min-width: 255px;
    margin-bottom: 20px;
    font-size: 16px;
    display: block;
    box-sizing: border-box;
    }
  
& select {
    background-color: #333;
    color: #fff;
    border: none;
    padding: 10px;
    border-radius: 5px;
    width: 100%;
    min-width: 255px;
    margin-bottom: 20px;
    display: block;
    box-sizing: border-box;
  }
  
  & .footer {
    display: flex;
  }
  
  & .footer button {
    background-color: white;
    color: #012367;
    display: block;
    border: 3px solid #012367;
    border-radius: 15px;
    padding: 10px;
    cursor: pointer;
    flex-grow: 1;
  }

   & #calories {
    border: ${(props) => props.caloriesCount < 0 && " 1px solid red"};
    background-color:${(props) => props.caloriesCount < 0 && " white"};   
    color:${(props) => props.caloriesCount < 0 && " red"}; 
}

@media (min-width: 768px) {
display: flex;
flex-wrap:w
  & input[type="text"],
  & input[type="number"],
  & input[type="date"],
  & select {
   
}


`;
function CaloriesRecordEdit(props) {

    const DEFAULT_VALUE = {
        date: "",
        meal: "",
        content: "",
        calories: "",
    }
    const [clickCounter, setClickCounter] = useState(0);
    const [mealRecord, setMealRecord] = useState(DEFAULT_VALUE);




    const onDateChangeHandler = (event) => {
        setMealRecord({
            ...mealRecord,
            date: event.target.value,
        })
    };
    const onMealChangeHandler = (event) => {
        setMealRecord({
            ...mealRecord,
            meal: event.target.value,
        })
    };
    const onContentChangeHandler = (event) => {
        setMealRecord({
            ...mealRecord,
            content: event.target.value,
        })
    };
    const onCalorieChangeHandler = (event) => {

        setMealRecord({
            ...mealRecord,
            calories: event.target.value,
        })
    }

    const onSubmitHandler = (event) => {
        event.preventDefault();
        console.log(mealRecord);
        props.onFormSubmit(mealRecord);
        setMealRecord(DEFAULT_VALUE);
    };


    return (


        <Form caloriesCount={mealRecord.calories} onSubmit={onSubmitHandler}>
            <label> I got clicked {clickCounter} times</label>
            <label htmlFor="date">Date: </label>
            <input value={mealRecord.date} type="date" id="date" onChange={onDateChangeHandler} />
            <label htmlFor="meal">Meal: </label>
            <select value={mealRecord.meal} id="meal" onChange={onMealChangeHandler}>
                <option value="Breakfast">Breakfast</option>
                <option value="Lunch">Lunch</option>
                <option value="Dinner">Dinner</option>
                <option value="Snack">Snack</option>
            </select>
            <label htmlFor="content" >Content: </label>
            <input value={mealRecord.content} type="text" id="content" onChange={onContentChangeHandler} />
            <label htmlFor="calories"  >Calories: </label>
            <input value={mealRecord.calories}
                type="number" id="calories" onChange={onCalorieChangeHandler} />

            <div className="footer">
                <button>Add Record</button>
                <ClickCounter
                    setClickCounter={setClickCounter} />
            </div>
        </Form>




















    )
}
export default CaloriesRecordEdit;
