import React, { useState } from 'react';
import RecordList from './RecordList';
import styles from "./ListingSection.module.css";

const ListingSection = (props) => {
  const { allRecords } = props;
  const [currentDate, setCurrentDate] = useState(new Date());

  const dateChangeHandler = (event) => {
    setCurrentDate(new Date(event.target.value));
  };

  // تحويل التاريخ إلى قيمة خالية من الوقت للمقارنة بشكل أدق وأسرع
  const normalizeDate = (date) => {
    const normalized = new Date(date);
    normalized.setHours(0, 0, 0, 0); // إزالة أي وقت مرتبط بالتاريخ
    return normalized;
  };

  const currentDateNormalized = normalizeDate(currentDate);

  const dateFilter = (record) => {
    const recordDateNormalized = normalizeDate(record.date);
    return recordDateNormalized.getTime() === currentDateNormalized.getTime();
  };

  return (
    <>
      <label className={styles["listing-picker-label"]} htmlFor='listingDate'>
        Select date
      </label>
      <input
        id='listingDate'
        type="date"
        value={currentDate.toISOString().split("T")[0]} // عرض التاريخ بتنسيق yyyy-mm-dd
        className={styles["listing-picker-input"]}
        onChange={dateChangeHandler}
      />
      <RecordList records={allRecords.filter(dateFilter)} /> 
    </>
  );
};

export default ListingSection;
