import React from 'react';

// import {RecordList} from './components/RecordList';
import RecordList from './components/CalorieRecords-Section/RecordList';
import CaloriesRecordEdit from './components/edit/CaloriesRecordEdit';
import { useState } from 'react';

const INITIAL_RECORDS = [{
  id:1,
  date: new Date(2023, 2, 1,),
  meal: "Breakfast",
  content: 'Eggs',
  calories: 111,
},
{
  id:2,
  date: new Date(2023, 2, 2,),
  meal: "Breakfast",
  content: 'Eggs',
  calories: 222,
},
{
  id:3,
  date: new Date(2023, 2, 3,),
  meal: "Breakfast",
  content: 'Eggs',
  calories: 340,
},
{
  id:4,
  date: new Date(2023, 2, 4,),
  meal: "Breakfast",
  content: 'Eggs',
  calories: 340,
},

]
function App() {
  const [records, setRecords] = useState(INITIAL_RECORDS);
  const [nextId, setNextId] = useState(5);

  const formSubmitHandler = (record) => {
    const formattedRecord = {
      ...record,
      date: new Date(record.date),
      id:nextId,
    };

    setNextId((lastVal) => lastVal + 1);
    setRecords((prevRecords) => [formattedRecord, ...prevRecords]);

  }
  return (
    <div>
      <h1>Welcome to React with Almdrasa!</h1>
      <CaloriesRecordEdit onFormSubmit={formSubmitHandler} />
      <RecordList records={records} />


    </div>
  )
}

export default App;
