import React, { useState } from 'react';
import ListingSection from './components/CalorieRecords-Section/ListingSection';
import CaloriesRecordEdit from './components/edit/CaloriesRecordEdit';
import Modal from 'react-modal';
import styles from './App.module.css';
import { getDateFromString } from './utils';

// بيانات افتراضية
const INITIAL_RECORDS = [
  { id: 1, date: new Date(2023, 2, 1).toISOString().split('T')[0], meal: "Breakfast", content: 'Eggs', calories: 111 },
  { id: 2, date: new Date(2023, 2, 2).toISOString().split('T')[0], meal: "Breakfast", content: 'Bread', calories: 150 },
  { id: 3, date: new Date(2023, 2, 3).toISOString().split('T')[0], meal: "Lunch", content: 'Milk', calories: 333 },
  { id: 4, date: new Date(2023, 2, 4).toISOString().split('T')[0], meal: "Dinner", content: 'Rice', calories: 444 },
];

function App() {
  const modalStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      border: 'none',
      padding: '0px',
      borderRadius: "var(--theme-border-radius-smooth)",
    },
    overlay: {
      backgroundColor: 'rgba(0,0,0,0.5)',
    }
  };

  const [records, setRecords] = useState(INITIAL_RECORDS);
  const [nextId, setNextId] = useState(5);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleOpenModal = () => {
    setIsModalOpen(true);
  }
  const handleCloseModal = () => {
    setIsModalOpen(false);
  }

  const formSubmitHandler = (record) => {
    const formattedRecord = {
      ...record,
      date: getDateFromString(record.date).toISOString().split('T')[0], // تنسيق التاريخ
      id: nextId,
    };

    setNextId((lastVal) => lastVal + 1);
    setRecords((prevRecords) => [formattedRecord, ...prevRecords]);
    handleCloseModal();
  };

  return (
    <div>
      <h1 className={styles.title}>Calorie Tracker</h1>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={handleCloseModal}
        contentLabel="Modal"
        style={modalStyles}
      >
        <CaloriesRecordEdit onFormSubmit={formSubmitHandler} onCancel={handleCloseModal} />
      </Modal>
      <ListingSection allRecords={records} />
      <button className={styles["open-modal-btn"]} onClick={handleOpenModal}>Track food </button>
    </div>
  );
}

export default App;
